import React, { useState } from 'react';
import axios from 'axios';

const CRMForm = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        phone: ''
    });

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();  // Prevent the default form submission behavior
        console.log('Form Submitted, Data:', formData);  // Log form data to check if it's being captured correctly

        try {
            const response = await axios.post(
                'http://localhost:8069/api/crm/create',
                JSON.stringify(formData),
                {
                    headers: {
                        'Content-Type': 'application/json'
                    }
                }
            );
            console.log('API Response:', response.data);  // Log the API response for debugging
            if (response.data.success) {
                alert('CRM Record created successfully!');
            } else {
                alert('Failed to create CRM record: ' + response.data.error);
            }
        } catch (error) {
            console.error('There was an error creating the CRM record!', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>Name:</label>
                <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    required
                />
            </div>
            <div>
                <label>Email:</label>
                <input
                    type="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                />
            </div>
            <div>
                <label>Phone:</label>
                <input
                    type="tel"
                    name="phone"
                    value={formData.phone}
                    onChange={handleChange}
                    required
                />
            </div>
            <button type="submit">Submit</button>
        </form>
    );
};

export default CRMForm;
