import React, { useState } from 'react';
import axios from 'axios';

const CRMForm = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:8069/api/crm/create', {
        name,
        email,
        phone,
      }, {
        headers: {
          'Authorization': 'Bearer dd11296dcd60f44a6821fd2549af188e0ec6e530',
          'Content-Type': 'application/json'
        }
      });

      if (response.data.success) {
        alert('CRM record created successfully!');
      } else {
        setError(response.data.error);
      }
    } catch (error) {
      console.error('There was an error creating the CRM record!', error);
      setError('There was an error creating the CRM record.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Name:</label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Email:</label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Phone:</label>
        <input
          type="tel"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
          required
        />
      </div>
      <button type="submit">Submit</button>
      {error && <p>{error}</p>}
    </form>
  );
};

export default CRMForm;
